mapper.update:
update 
	synthesisknife 
set 
	DelFlag = '1',
	UpdateTime = NOW(),
	UpdateUser = #{sk1.updateUser}
where
	SynthesisParametersCode = #{sk1.synthesisParametersCode}
and 
    SynthesisParametersNumber = #{sk1.synthesisParametersNumber}

mapper.select:
SELECT s.SynthesisParametersCode synthesisParametersCode ,s.SynthesisCutterNumber synthesisCutterNumber ,s.SynthesisParametersNumber synthesisParametersNumber ,s.BusinessFlowLnkID businessFlowLnkID ,s.EquipmentID equipmentID ,s.KnifeInventoryCode knifeInventoryCode ,s.LoadState loadState ,s.OffloadType offloadType ,s.InstallFlag installFlag ,s.RFID rFID ,s.XPoint xPoint ,s.YPoint yPoint ,s.KnifeMan knifeMan ,sp.CreateType createType from synthesisknife s inner join synthesisparameters sp on s.SynthesisParametersCode = sp.SynthesisParametersCode where s.DelFlag = '0' and sp.DelFlag = '0'
and s.rFID = #{skentity.rFID}
and s.SynthesisParametersCode = #{skentity.synthesisParametersCode}
limit 1
